import React from 'react'
import Post from './Post/Post'
import s from './myPosts.module.css'


const MyPosts = (props) => {
    let postsElements = props.posts.map(p=><Post message={p.message} likesCount = {p.likesCount} id={'1234'}/>);
    let textField1 = React.createRef();

    let onAddPost = () => props.addPost()

    let onPostChange = () => {
        let text = textField1.current.value;
        props.updateNewPostText(text)
    }

    return <div className={s.postsBlock}>
        <h3>My post</h3>
        <div>
        <div><textarea onChange={onPostChange} ref={textField1} value={props.newPostText}></textarea></div>
            <div><button onClick = {onAddPost}> Add Post </button>
            <button> Post remove </button></div>
        </div>
        <div className={s.posts}>
            {postsElements}
        </div>
    </div>
}

export default MyPosts
