import React from "react"
import Profile from './Profile'
import { getUserProfile} from "../Redux/profileReducer"
import { connect } from "react-redux";
import { Navigate, useLocation, useNavigate, useParams } from "react-router-dom";
import {withAuthRedirect} from "../Hoc/WithAuthRedirect"
import { compose } from "redux";

class ProfileContainer extends React.Component {

    componentDidMount() {
        let userId = this.props.router.params.userId
        if (!userId) { userId = 30773 }
        this.props.getUserProfile(userId)
    }

    render() { return <Profile {...this.props} profile={this.props.profile} /> }
}
function withRouter(Component) { // дописал с комментов
    function ComponentWithRouterProp(props) {
        let location = useLocation();
        let navigate = useNavigate();
        let params = useParams();
        return (
            <Component
                {...props}
                router={{ location, navigate, params }}
            />
        );
    }

    return ComponentWithRouterProp;
}
let mapStateToProps = (state) => ({ profile: state.profilePage.profile })
export default compose(connect(mapStateToProps, { getUserProfile }), withRouter, 

