import s from "./Users.module.css"
import ph from "../../assets/images/tetatet.jpeg"
import React from "react";

let Users = (props) => {

    let pagesCount = Math.ceil( this.props.totalUsersCount / this.props.pageSize );
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) { pages.push(i) };

    return <div>
    {/* <button onClick={this.getUsers}> Request server </button> */}
    <div>
      {" "}
      {pages.map((p) => {
        return (
          <span
            onClick={(e) => this.onPageChenged(p)}
            className={this.props.currentPage === p && s.selectedPage}
          >
            {" "}
            {p}{" "}
          </span>
        );
      })}
    </div>
    {this.props.users.map((u) => (
      <div key={u.id}>
        <span>
          <div className={s.ava}>
            <img src={u.photos.small != null ? u.photos.small : ph} />
          </div>
          <div className={s.btn}>
            {u.followed ? (
              <button
                onClick={() => {
                  this.props.unfollow(u.id);
                }}
              >
                {" "}
                Unfollow{" "}
              </button>
            ) : (
              <button
                onClick={() => {
                  this.props.follow(u.id);
                }}
              >
                {" "}
                Follow{" "}
              </button>
            )}
          </div>
        </span>
        <span>
          <span>
            <div>{u.name}</div>
            {u.status}
            <div></div>
          </span>
          <span>
            <div>{u.id}</div>
            <div>{"u.location.city"}</div>
          </span>
        </span>
      </div>
    ))}
  </div>;
};

export default Users
