import React from 'react'
import Post from './Post/Post'
import s from './myPosts.module.css'

const MyPosts = (props) => {
    let postsElements = props.posts.map(p=><Post message={p.message} likesCount = {p.likesCount}/>);

    let textField1 = React.createRef();
    let callback = () => {
        let text = textField1.current.value;
        props.addPost(text)
        text = ''
    }

    return <div className={s.postsBlock}>
        <h3>My post</h3>
        <div>
        <div><textarea ref={textField1}></textarea></div>
            <div><button onClick = {callback}>Adds Post</button>
            <button>Post remove</button></div>
        </div>
        <div className={s.posts}>
            {postsElements}
        </div>
    </div>
}

export default MyPosts
