import React from "react"
import { Field, reduxForm } from "redux-form"
import { Input } from "../common/FormControls"
import { required } from '../../utilits/validators/validators';
import { connect } from "react-redux";
import { login } from "../Redux/authReducer";

const LoginForm = (props) => {
return (
<form onSubmit={props.handleSubmit}>
<div><Field placeholder={"Email"} name={"email"} component={Input} validate={[required]}/></div>
<div><Field placeholder={"Password"} name={"Password"} component={Input} validate={[required]}/></div>
<div><Field component={Input} name={"Remember me"} type={"checkbox"}/>Remember me</div>
<div><button>Submit</button></div>
</form>)}

const LoginReduxForm = reduxForm ({form: "login"})(LoginForm)

const Login = (props) => {
    const onSubmit = (formdata) => props.login(formdata.email, formdata.password, formdata.rememberMe)
    return <div>
        <h1>LogiN</h1>
        <LoginReduxForm onSubmit={onSubmit}/>
    </div>
}

export default connect(null, {login})(Login)
