import React from "react"
import Users from "./Users"
import Preloader from "../common/preloader";
import { connect } from "react-redux";
import { follow, unfollow, setCurrentPage, toggleFollowingProgress, getUsers} from "../Redux/usersReducer";

class UsersComponent extends React.Component {

   componentDidMount() {
      this.props.getUsers(this.props.currentPage,this.props.pageSize)
   }

   onPageChenged = (pageNumber) => {
      this.props.setCurrentPage(pageNumber);
      this.props.getUsers(this.props.currentPage,this.props.pageSize)
   }

   /* onPageChenged = (pageNumber) => {
      this.props.setCurrentPage(pageNumber);
      this.props.toggleIsFetching(true)
      usersAPI.getUsers(pageNumber, this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false)
            this.props.setUsers(data.items)
         })
   } */

   render() {return <> {this.props.isFetching 
      ? <Preloader /> 
      : null} 
      <Users
         totalUsersCount={this.props.totalUsersCount}
         pageSize={this.props.pageSize}
         currentPage={this.props.currentPage}
         users={this.props.users}
         unfollow={this.props.unfollow}
         follow={this.props.follow}
         onPageChenged={this.onPageChenged}
      /> </>}
}
let mapStateToProps = (state) => {
   return {
      users: state.usersPage.users,
      pageSize: state.usersPage.pageSize,
      totalUsersCount: state.usersPage.totalUsersCount,
      currentPage: state.usersPage.currentPage,
      isFetching: state.usersPage.isFetching,
      followingInProgress: state.usersPage.followingInProgress,
   }
};

export default connect (mapStateToProps, { follow, unfollow, setCurrentPage,toggleFollowingProgress, getUsers}) (UsersComponent)
