import { usersAPI } from "../../API/api";
import { profoleAPI } from "../../API/api";

const ADD_POST = "ADD-POST";
const UPDATE_NEW_POST_TEXT = "UPDATE-NEW-POST-TEXT";
const SET_USER_PROFILE = "SET_USER_PROFILE"
const SET_STATUS = "SET_STATUS"

let initialState = {
  posts: [
    { id: 1, likesCount: 12, message: "Hello, BRO" },
    { id: 2, likesCount: 4, message: "Hello, B" },
    { id: 3, likesCount: 51, message: "Hell Oy" },
    { id: 4, likesCount: 614, message: "airuqp ppq uqweh b" },
    { id: 5, likesCount: 111, message: "It`s my first post!" },
  ],
  newPostText: "fuck!",
  profile: null,
  status: "",
};

const profileReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      let newPost = {
        id: 5,
        message: state.newPostText,
        likesCount: 0,
      };
      return { ...state, posts: [...state.posts, newPost], newPostText: "" };
    case UPDATE_NEW_POST_TEXT:
      return { ...state, newPostText: action.newText };
    case SET_USER_PROFILE:
      return { ...state, profile: action.profile };
    case SET_STATUS:
      return { ...state, status: action.status };
    default:
      return state;
  }
};

export let addPostActionCreator = () => ({ type: ADD_POST });
export let setUserProfile = (profile) => ({ type: SET_USER_PROFILE, profile});
export let setStatus = (status) => ({ type: SET_STATUS, status});

export let getUserProfile = (userId) => (dispatch) => { usersAPI.getProfile(userId) // thunk
  .then(response => dispatch(setUserProfile(response.data)))};

export let getStatus = (userId) => (dispatch) => { profileAPI.getStatus(userId) // thunk to
  .then(response => dispatch(setStatus(response.data)))}; // video 73 / 14 min

export let updateNewPostTextActionCreator = (text) => ({type: UPDATE_NEW_POST_TEXT, newText: text});

export default profileReducer;
