import s from "./Users.module.css"
import ph from "../../assets/images/tetatet.jpeg"
import React from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

let Users = (props) => {

  let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
  let pages = [];
  for (let i = 1; i <= 10; i++) { pages.push(i) }; // вместо 10 был pagesCount

  return <div>
    <div>{pages.map((p) => {
      return (
        <span onClick={(e) => props.onPageChenged(p)}
          className={props.currentPage === p && s.selectedPage}>{p}</span>)
    })}
    </div>
    {props.users.map((u) => (
      <div key={u.id}>
        <span>
          <div className={s.ava}>
            <NavLink to={"/profile/" + u.id}> <img src={u.photos.small != null ? u.photos.small : ph} /> </NavLink>
          </div>
          <div className={s.btn}>{u.followed
            ? <button disabled={props.followingInProgress.some(id => id == u.id)} onClick={() => {
              props.toggleFollowingProgress(true, u.id);
              axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`, {
                withCredentials: true,
                headers: { "API-KEY": "1721d0bf-4bc2-4cee-8d7e-6d0004333236" }
              })
                .then(response => {
                  if (response.data.resultCode == 0) {
                    props.unfollow(u.id)
                  }
                  props.toggleFollowingProgress(false, u.id)
                })
            }}> Unfollow </button>
            : <button disabled={props.followingInProgress.some(id => id == u.id)} onClick={() => {
              props.toggleFollowingProgress(true, u.id);
              axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`, {}, {
                withCredentials: true,
                headers: { "API-KEY": "1721d0bf-4bc2-4cee-8d7e-6d0004333236" }
              })
                .then(response => {
                  if (response.data.resultCode == 0) {
                    props.follow(u.id)
                  }
                  props.toggleFollowingProgress(false, u.id)
                })
            }}> Follow </button>}
          </div>
        </span>
        <span>
          <span>
            <div>{u.name}</div>
            {u.status}
            <div></div>
          </span>
          <span>
            <div>{u.id}</div>
            <div>{"u.location.city"}</div>
          </span>
        </span>
      </div>
    ))}
  </div>;
};

export default Users
