import React, { Component } from "react"
import s from "../common/FormControls.module.css"
import { Field, reduxForm } from "redux-form"
import { сreateField, Input } from "../common/FormControls"
import { required } from '../../utilits/validators/validators';
import { connect } from "react-redux";
import { login } from "../Redux/authReducer";
import { Navigate } from "react-router-dom";

const LoginForm = ({ handleSubmit, error }) => {
    debugger
    return (
        <form onSubmit={handleSubmit}>
            <Field name="123" validate={[required]} component={Input} />
            {сreateField("Email", "email", [required], Input)}
            {сreateField("Password", "password", [required], Input, { type: "password" })} {/* 90 - redux-ducks рефакторинг 38 min */}
            {сreateField(null, "Remember me", [], Input, { type: "checkbox" }, "Remember me")}
            {error && <div className={s.formSummaryError}>{error}</div>}
            <div><button>Submit</button></div></form>)
}

const LoginReduxForm = reduxForm({ form: "login" })(LoginForm)

const Login = (props) => {
    const onSubmit = (formdata) => { props.login(formdata.email, formdata.password, formdata.rememberMe) }
    if (props.isAuth) return <Navigate to={"/profile"} />
    return <div>
        <h1>LogiN</h1>
        <LoginReduxForm onSubmit={onSubmit} />
    </div>
}
const mapStateToProps = (state) => ({ isAuth: state.auth.isAuth })
export default connect(mapStateToProps, { login })(Login)
