const UPDATE_NEW_MESSAGE_BODY = "UPDATE_NEW_MESSAGE_BODY";
const SEND_MESSAGE = "SEND_MESSAGE";

let initialState = {
  dialogs: [
    {
      name: "Diman",
      id: 1,
      photo:
        "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
    },
    {
      name: "Roman",
      id: 2,
      photo:
        "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
    },
    {
      name: "Samochka olga",
      id: 3,
      photo:
        "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
    },
    {
      name: "Serdey",
      id: 4,
      photo:
        "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
    },
    {
      name: "No name hero",
      id: 5,
      photo:
        "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
    },
  ],
  message: [
    { id: 1, message: "Hello!" },
    { id: 2, message: "apolinapolis, its realy you?!" },
    { id: 3, message: "i`m youre friend to!" },
    { id: 4, message: "WAZZZZAP!" },
    { id: 5, message: "Ohh... its Grossartich!" },
  ],
  newMessageBody: "",
}

const dialogsReducer = (state = initialState, action) => {
  stateCopy.message = [...state.message]
  switch (action.type) {
    case UPDATE_NEW_MESSAGE_BODY:{
      let stateCopy = {...state}
      stateCopy.newMessageBody = action.body;
      return stateCopy;}
    case SEND_MESSAGE:{
      let stateCopy = {...state}
      let body = stateCopy.newMessageBody;
      stateCopy.newMessageBody = "";
      stateCopy.message.push({ id: 6, message: body });
      return stateCopy;}
    default:
      return state;
  }
};

export const sendMessageCreator = () => ({ type: SEND_MESSAGE });
export const updateNewMessageBodyCreator = (text) => ({
  type: UPDATE_NEW_MESSAGE_BODY,
  body: text,
});

export default dialogsReducer;
