import React from 'react'

class ProfileStatus extends React.Component {
    statusInputRef = React.createRef()
    state = {
        editMode: false,
        status: this.props.status,
    }

    activateEditMode = () => {this.setState({editMode: true})}
    deactivateEditMode = () => {
        this.setState({editMode: false})
        this.props.updateStatus(this.statusInputRef.current.value)
}

    render() {
        return <>
            <div>{!this.state.editMode && <div><span onClick={this.activateEditMode}>{this.props.status}</span></div>}
                {this.state.editMode && <div><input ref={this.statusInputRef} autoFocus={true} onBlur={this.deactivateEditMode} value={this.props.status} /></div>}</div>
        </>
    }
}

export default ProfileStatus
