import React from 'react'
import Post from './Post/Post'
import s from './myPosts.module.css'

const MyPosts = (props) => {
    let postsElements = props.posts.map(p=><Post message={p.message} likesCount = {p.likesCount}/>);

    let textField1 = React.createRef();

    let addPost = () => {
        props.dispatch({type: 'ADD-POST'})
    }

    let postChange = () => {
        let text = textField1.current.value;
        let action = {type: 'UPDATE-NEW-POST-TEXT' , newText: text}
        props.dispatch(action)

    }

    return <div className={s.postsBlock}>
        <h3>My post</h3>
        <div>
        <div><textarea onChange={postChange} ref={textField1} value={props.textChange}></textarea></div>
            <div><button onClick = {addPost}>Adds Post</button>
            <button>Post remove</button></div>
        </div>
        <div className={s.posts}>
            {postsElements}
        </div>
    </div>
}

export default MyPosts
