import React from "react"
import s from "./Users.module.css"
import axios from "axios"
import ph from "../../assets/images/tetatet.jpeg"

class Users extends React.Component {

   constructor(props) {
      super(props);
         axios.get("https://social-network.samuraijs.com/api/1.0/users")
            .then(response => { this.props.setUsers(response.data.items) })
   };

   render() {
      return <div><button onClick={this.getUsers}>'Request server'</button>
         {this.props.users.map(u => <div key={u.id}>
            <span>
               <div className={s.ava}>
                  <img src={u.photos.small != null ? u.photos.small : ph} />
               </div>
               <div className={s.btn}>
                  {u.followed
                     ? <button onClick={() => { this.props.unfollow(u.id) }}> Unfollow </button>
                     : <button onClick={() => { this.props.follow(u.id) }}> Follow </button>}
               </div>
            </span>
            <span>
               <span><div>{u.name}</div>{u.status}<div></div></span>
               <span><div>{u.id}</div><div>{"u.location.city"}</div></span>
            </span>
         </div>)}
      </div>
   }
}

export default Users
