import React from "react"
import s from "./Users.module.css"
import axios from "axios"
import ph from "../../assets/images/tetatet.jpeg"

class Users extends React.Component {

   componentDidMount() {
      axios.get(`https://social-network.samuraijs.com/api/1.0/users?page${this.props.currentPage}&count=${this.props.pageSize}`)
         .then(response => { this.props.setUsers(response.data.items) })
   }

   onPageChenged = (pageNumber) => {this.props.setCurrentPage(pageNumber);
      axios.get(`https://social-network.samuraijs.com/api/1.0/users?page${pageNumber}&count=${this.props.pageSize}`)
      .then(response => { this.props.setUsers(response.data.items) })}

   render() {

      let pagesCount = Math.ceil( this.props.totalUsersCount / this.props.pageSize );
      let pages = [];
      for (let i = 1; i <= pagesCount; i++) { pages.push(i) }

      return <div><button onClick={this.getUsers}> Request server </button>
         <div> {pages.map( p => {
            return <span onClick={ (e) => this.onPageChenged(p) } className={this.props.currentPage === p && s.selectedPage}> {p} </span>
         })}
         </div>
         {this.props.users.map(u => <div key={u.id}>
            <span>
               <div className={s.ava}>
                  <img src={u.photos.small != null ? u.photos.small : ph} />
               </div>
               <div className={s.btn}>
                  {u.followed
                     ? <button onClick={() => { this.props.unfollow(u.id) }}> Unfollow </button>
                     : <button onClick={() => { this.props.follow(u.id) }}> Follow </button>}
               </div>
            </span>
            <span>
               <span><div>{u.name}</div>{u.status}<div></div></span>
               <span><div>{u.id}</div><div>{"u.location.city"}</div></span>
            </span>
         </div>)}
      </div>
   }
}

export default Users
