import s from './Dialogs.module.css'
import Message from './Message/Message';
import DialogItem from './DialogItem/DialogItem';
import React from 'react';
import { updateNewMessageBodyCreator, sendMessageCreator } from '../Redux/dialogsReducer';

const Dialogs = (props) => {
    //let state = props.store.getState().dialogsPage
    let dialogsElements = state.dialogs.map(dialog => <DialogItem name={dialog.name} id={dialog.id} photo = {dialog.photo} />)
    let messageElements = state.message.map(mess => <Message className={s.message} message={mess.message} />)
    let newMessageBody = state.newMessageBody
    let target = React.createRef()
    let onSendMessageClick = () => props.sendMessage()
    let onNewMessageChange = (e) => { 
        let body = e.target.value;
        props.updateNewMessageBody(body)
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                <div>{messageElements}</div>
                <div>
                    <div><textarea value={newMessageBody} onChange={onNewMessageChange} placeholder='vvodi svoy' cols="30" rows={4} ref={target}></textarea></div>
                    <div><button onClick={onSendMessageClick}>Send</button></div>
                </div>
            </div>
        </div>
    )

}

export default Dialogs
