//import { act } from "react-dom/test-utils";

const ADD_POST = "ADD-POST";
const UPDATE_NEW_POST_TEXT = "UPDATE-NEW-POST-TEXT";

const UPDATE_NEW_MESSAGE_BODY = "UPDATE_NEW_MESSAGE_BODY";
const SEND_MESSAGE = "SEND_MESSAGE";

let store = {
  _state: {
    profilePage: {
      posts: [
        { id: 1, likesCount: 12, message: "Hello, BRO" },
        { id: 2, likesCount: 4, message: "Hello, BRrrrrr" },
        { id: 3, likesCount: 51, message: "Hell Oy" },
        { id: 4, likesCount: 614, message: "airuqp ppq uqweh b" },
        { id: 5, likesCount: 111, message: "It`s my first post!" },
      ],
      newPostText: "fuck! eah",
    },

    dialogsPage: {
      dialogs: [
        {
          name: "Diman",
          id: 1,
          photo:
            "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
        },
        {
          name: "Roman",
          id: 2,
          photo:
            "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
        },
        {
          name: "Samochka olga",
          id: 3,
          photo:
            "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
        },
        {
          name: "Serdey",
          id: 4,
          photo:
            "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
        },
        {
          name: "No name hero",
          id: 5,
          photo:
            "https://thumbs.dreamstime.com/b/го-ова-бойскаута-м-а-шей-группы-90271267.jpg",
        },
      ],
      message: [
        { id: 1, message: "Hello!" },
        { id: 2, message: "apolinapolis, its realy you?!" },
        { id: 3, message: "i`m youre friend to!" },
        { id: 4, message: "WAZZZZAP!" },
        { id: 5, message: "Ohh... its Grossartich!" },
      ],
      newMessageBody: "",
    },
    sidebar: {
      friends: [{ name: "Sergey" }, { name: "Larra" }, { name: "Olav" }],
    },
  },
  _callSubscriber() {alert("called subscriber on State.js / store")},
  getState() { return this._state},
  subscribe(observer) {this._callSubscriber = observer},

  dispatch(action) {
    if (action.type == ADD_POST) {
      let newPost = {
        id: 5,
        message: this._state.profilePage.newPostText,
        likesCount: 0,
      };
      this._state.profilePage.posts.push(newPost);
      this._state.profilePage.newPostText = "";
      this._callSubscriber(this._state);
    } else if (action.type == UPDATE_NEW_POST_TEXT ) {
      this._state.profilePage.newPostText = action.newText;
      this._callSubscriber(this._state);
    } else if (action.type == UPDATE_NEW_MESSAGE_BODY) {
      this._state.dialogsPage.newMessageBody = action.body;
      this._callSubscriber(this._state);
    } else if (action.type == SEND_MESSAGE) {
      let body = this._state.dialogsPage.newMessageBody
      this._state.dialogsPage.newMessageBody = "";
      this._state.dialogsPage.message.push({ id: 6, message: body });
      this._callSubscriber(this._state);
    }
  },
};

export let addPosrActionCreator = () => ({type: ADD_POST});
export let updateNewPostTextActionCreator = (text) => ({ type: UPDATE_NEW_POST_TEXT, newText: text })

export const sendMessageCreator = () => ({type: SEND_MESSAGE })
export const updateNewMessageBodyCreator = (text) => ({type: UPDATE_NEW_MESSAGE_BODY, newText: text})

export default store;
