import React from "react"
import Message from "./Message"
import s from './Dialogs.module.css'
import { Field, reduxForm } from "redux-form"
Message

const StartMessageField = (props) => {
    let state = props.dialogsPage
    let messageElements = state.message.map(mess => <Message className={s.message} message={mess.message} />)
    let newMessageBody = state.newMessageBody
    let target = React.createRef()

    let onSendMessageClick = () => props.onSendMessageClick();
    let onNewMessageChange = (e) => {
        let body = e.target.value;
        props.updateNewMessageBody(body)
    }

    return <div className={s.messages}>
        <div>{messageElements}</div>
        <div>
            <div><Field component={'textarea'} value={newMessageBody} onChange={onNewMessageChange}
                placeholder='vvodi svoy' cols="30" rows={4} name={"testField"} ref={target} /></div>
            <div><button onClick={onSendMessageClick}>Send</button></div>
        </div>
    </div>
}

const MessageReduxForm = reduxForm({ form: "login" })(StartMessageField)

const onSubmit = (formdata) => { console.log(formdata); }

const MessageField = (props) => {
    return <div>
        <h1>MESSAGES</h1>
        <MessageReduxForm onSubmit={onSubmit} />
    </div>
}

export default MessageField
