import s from './Dialogs.module.css'
import DialogItem from './DialogItem/DialogItem';
import React from 'react';
import { Navigate } from 'react-router-dom';
import Message from "./Message/Message"
import { Field, reduxForm } from "redux-form"




const Dialogs = (props) => {
    let state = props.dialogsPage;
    let dialogsElements = state.dialogs.map(dialog => <DialogItem name={dialog.name} id={dialog.id} photo={dialog.photo} />)
    let messageElements = state.message.map(mess => <Message className={s.message} message={mess.message} id={'123'} />)
    let newMessageBody = state.newMessageBody
    let target = React.createRef()

    const onSubmit = (formdata) => { console.log(formdata); }
    let onSendMessageClick = () => props.onSendMessageClick();
    let onNewMessageChange = (e) => {
        let body = e.target.value;
        props.updateNewMessageBody(body)
    }

    if (!props.isAuth) { return <Navigate to={"/login"} /> }

    const StartMessageField = () => {
        return <form onSubmit={props.handleSubmit}>
            <div><Field component={'textarea'} value={newMessageBody} onChange={onNewMessageChange}
                placeholder='vvodi svoy' cols="30" rows={4} name={"testField"} ref={target} /></div>
            <div><button onClick={onSendMessageClick}>Send</button></div>
        </form>
    }
    const MessageReduxForm = reduxForm({ form: "mess" })(StartMessageField)

    const MessageField = (props) => {
        return <div>
            <h1>MESSAGES</h1>
            <MessageReduxForm onSubmit={onSubmit} />
        </div>
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>{dialogsElements}</div>
            <div className={s.messages}>
            <div>{messageElements}</div>
            <MessageField/>
            </div>
        </div>
    )

}





export default Dialogs
