import { usersAPI, profileAPI } from "../../API/api";

const ADD_POST = "ADD-POST";
const SET_USER_PROFILE = "SET_USER_PROFILE"
const SET_STATUS = "SET_STATUS"

let initialState = {
  posts: [
    { id: 1, likesCount: 12, message: "Hello, BRO" },
    { id: 2, likesCount: 4, message: "Hello, B" },
    { id: 3, likesCount: 51, message: "Hell Oy" },
    { id: 4, likesCount: 614, message: "airuqp ppq uqweh b" },
    { id: 5, likesCount: 111, message: "It`s my first post!" }],
    profile: null,
    status: "it is status in profileReduser",
};

const profileReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      let newPost = {
        id: 5,
        message: action.myPostText,
        likesCount: 0,
      };
      return { ...state, posts: [...state.posts, newPost], newPostText: "" };
    case SET_USER_PROFILE:
      return { ...state, profile: action.profile };
    case SET_STATUS:
      return { ...state, status: action.status };
    default:
      return state;
  }
};

export const addPostActionCreator = (myPostText) => ({ type: ADD_POST,myPostText });
export const setUserProfile = (profile) => ({ type: SET_USER_PROFILE, profile});
export const setStatus = (status) => ({ type: SET_STATUS, status});

export const getUserProfile = (userId) => (dispatch) => { usersAPI.getProfile(userId) // thunk
  .then(response => dispatch(setUserProfile(response.data)))};

export const getStatus = (userId) => (dispatch) => { profileAPI.getStatus(userId) // thunk to
  .then(response => dispatch(setStatus(response.data)))}

export const updateStatus = (status) => (dispatch) => { profileAPI.updateStatus(status)
  .then(response => { if (response.data.resultCode === 0){ dispatch(setStatus(status))}}) }

export default profileReducer;
