import React, { useState } from 'react'

const ProfileStatusWithHooks = (props) => {

    let [editMode, setEditMode] = useState(false)
    let [status, setStatus] = useState(props.status)
    let activateEditmode = () => { setEditMode(true) }
    let deactivateEditmode = () => { setEditMode(false) }

    return <>
        <div>{!editMode
            && <div><span onDoubleClick={activateEditmode} >{props.status + "yooooou"} </span></div>}
            {editMode
            && <div><input autoFocus={true} onBlur={deactivateEditmode}/></div>}</div>
    </>
}

export default ProfileStatusWithHooks
