import React from "react"
import axios from "axios"
import Profile from './Profile'
import { setUserProfile } from "../Redux/profileReducer"
import { connect } from "react-redux";
import { useLocation, useNavigate, useParams } from "react-router-dom";

class ProfileContainer extends React.Component {

    userId = this.props.router.params.userId // коммент в моем исполнении 

    componentDidMount() {
debugger
        if (!this.userId) {this.userId = '2'}
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile/${this.userId}?`)
            .then(response => { this.props.setUserProfile(response.data) })
    }
    render() { return <Profile {...this.props} profile={this.props.profile} /> }
}

let mapStateToProps = (state) => ({ profile: state.profilePage.profile })


function withRouter(Component) { // дописал с комментов
    function ComponentWithRouterProp(props) {
        let location = useLocation();
        let navigate = useNavigate();
        let params = useParams();
        return (
            <Component
                {...props}
                router={{ location, navigate, params }}
            />
        );
    }

    return ComponentWithRouterProp;
}

export default connect(mapStateToProps, { setUserProfile })(withRouter(ProfileContainer))
