import s from './Dialogs.module.css'
import Message from './Message/Message';
import DialogItem from './DialogItem/DialogItem';
import React from 'react';

const Dialogs = (props) => {
    let dialogsElements = props.dialogs.map(dialog => <DialogItem name={dialog.name} id={dialog.id} photo = {dialog.photo} />)
    let messageElements = props.message.map(mess => <Message className={s.message} message={mess.message} />)
    let target = React.createRef()
    function showTargetmessage () {
        alert(target.current.value)
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>
            <div className={s.messages}>
                {messageElements}
            </div>
            <div><textarea cols="30" rows={4} ref={target}></textarea><button onClick={showTargetmessage}>Send</button><button>Clear</button></div>
        </div>
    )

}

export default Dialogs
