import React, { useState } from 'react'

const ProfileStatusWithHooks = (props) => {

    let [editMode, setEditMode] = useState(false)
    let [status, setStatus] = useState(props.status)
    let activateEditmode = () => { setEditMode(true) }
    let deactivateEditmode = () => { setEditMode(false); props.updateStatus(status)}
    const onStatusChange = (e) => {setStatus(e.currentTargetValue)}
    return <>
       <div>{function Counter() {
  const [count, setCount] = useState(0);

  function handleAlertClick() {
    setTimeout(() => {
      alert('You clicked on: ' + count);
    }, 3000);
  }

  return (
    <div>
      <p>You clicked {count} times</p>
      <button onClick={() => setCount(count + 1)}>
        Click me
      </button>
      <button onClick={handleAlertClick}>
        Show alert
      </button>
    </div>
  );
}}{
       !editMode
            && <div><span onDoubleClick={activateEditmode} >{props.status} </span></div>}
            {editMode
            && <div><input onChange={onStatusChange} value={status}
            autoFocus={true} onBlur={deactivateEditmode}/></div>}</div> 
    </>
}

export default ProfileStatusWithHooks
