const SET_USER_DATA = "SET_USER_DATA"

let initialState = {
  usersId: null,
  emale: null,
  login: null,
  isAuth: false,
};

const authReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER_DATA:
      return {...state,...action.data}
    default: return state
}}

export const setUserData = (userId, emale, login ) => ({type: SET_USER_DATA, data: {userId, emale,login} })
export default authReducer;
