const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS = '"SET_USERS';

let initialState = {
  users: [/* 
    {
      id: 1,
      photoUrl: "https://android-obzor.com/wp-content/uploads/2022/03/28e4ac42f547e6ac0f50f7cfa916ca93.jpg",
      followed: false,
      fullName: "Pozdeev",
      status: "Будем тусиииить",
      location: { city: "Izhevsk", country: "russia" },
    },
    {
      id: 2,
      photoUrl: 'https://webmg.ru/wp-content/uploads/2022/10/i-26-125-768x1169.jpeg',
      followed: true,
      fullName: "John Conor",
      status: "i will be back!",
      location: { city: "London", country: "GB" },
    },
    {
      id: 3,
      photoUrl: 'https://webmg.ru/wp-content/uploads/2022/10/i-26-125-768x1169.jpeg',
      followed: false,
      fullName: "Massiv",
      status: "i love this world",
      location: { city: "Objeckwill", country: "Immaginarium" },
    },
   */],
};

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLLOW:
      return {
        ...state,
        users: state.users.map(u => {
          if (u.id === action.userId) {
            return { ...u, followed: true }
          }
          return u;
        }),
      };
    case UNFOLLOW:
      return {
        ...state,
        users: state.users.map(u => {
          if (u.id === action.userId) {
            return { ...u, followed: false }
          }
          return u;
        }),
      };
    case SET_USERS:
      return { ...state, users: [...state.users, ...action.users] }
    default:
      return state;
  }
};

export const followAC = (userId) => ({ type: FOLLOW , userId});
export const unfollowAC = (userId) => ({ type: UNFOLLOW , userId});
export const setUsersAC = (users) => ({ type: SET_USERS , users});

export default usersReducer;
