import React, { useEffect } from "react";
import Profile from './Profile'
import { getUserProfile, getStatus, updateStatus} from "../Redux/profileReducer"
import { connect } from "react-redux";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { compose } from "redux";

class ProfileContainer extends React.Component {

    refreshProfile() {
        //let userId = this.props.router.params.userId
        let userId = this.props.profile.userId
        if (!userId) { userId = this.props.autorizedUserId }
        this.props.getUserProfile(userId)
        this.props.getStatus(userId)
     };

    componentDidMount() {
    this.refreshProfile()
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if(this.props.profile.userId != prevProps.profile.userId)
        this.refreshProfile()
    }

    render() { return <Profile 
        {...this.props} profile={this.props.profile} status={this.props.status} updateStatus={this.props.updateStatus} /> }
}

function withRouter(Component) { // old
    function ComponentWithRouterProp(props) {
        let location = useLocation();
        let navigate = useNavigate();
        let params = useParams();
        useEffect(() => {
            if (!props.isAuth) {
              navigate("/login");
            }
          }, [props.isAuth, navigate]);
        return (
            <Component
                {...props}
                router={{ location, navigate, params }}
            />
        );
    }

    return ComponentWithRouterProp;
}

let mapStateToProps = (state) => {
   return ({ 
    profile: state.profilePage.profile, 
    status: state.profilePage.status,
    autorizedUserId: state.auth.userId,
    isAuth: state.auth.isAuth,
})}

export default compose(connect(mapStateToProps, { getUserProfile, getStatus, updateStatus}), withRouter) (ProfileContainer)
