import s from './Post.module.css';

this.count = 0
const Post = (props) => {
    let likesBtn = document.getElementById('likesBtn');
    likesBtn.onclick = props.count++

    return <div className={s.item}>
        <img src="https://android-obzor.com/wp-content/uploads/2022/03/28e4ac42f547e6ac0f50f7cfa916ca93.jpg" alt="ops!" />
        {props.message}
        <div><button id='likesBtn'>LIKE</button><span>{` ${this.count} Likes`}</span></div>
    </div>
}

export default Post
